  loading = false;
  successMsg = '';
  errorMsg = '';
  contactUsForm!: FormGroup;
  private fb = inject(FormBuilder);
  private contactService = inject(ContactService);

  constructor() {}
  ngOnInit() {
    this.initForm();
  }

  ngOnDestroy(): void {
    this.dismissModals();
  }

  @HostListener('window:popstate')
  onBrowserBack(): void {
    this.dismissModals();
  }

  initForm() {
    this.contactUsForm = this.fb.group({
      fullName: ['', Validators.required],
      companyName: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      phone: ['', Validators.required],
      issue: ['', Validators.required],
      channel: ['', Validators.required],
      request: ['', Validators.required],
      terms: [false, Validators.requiredTrue],
    });
  }

  contactUs() {
    this.successMsg = '';
    this.errorMsg = '';

    const formContact = this.contactUsForm.value;
    if (!formContact.terms) {
      this.errorMsg = 'Debes aceptar los términos y condiciones.';
      return;
    }
    this.loading = true;
    this.contactUsForm.disable({ emitEvent: false });

    this.contactService.sendMail(formContact).subscribe({
      next: () => {
        this.loading = false;
        this.contactUsForm.enable({ emitEvent: false });
        this.successMsg = '¡Mensaje enviado con éxito! Nos pondremos en contacto contigo pronto.';
        this.contactUsForm.reset({ terms: false });
        this.hideModal();
      },
      error: () => {
        this.loading = false;
        this.contactUsForm.enable({ emitEvent: false });
        this.errorMsg =
          'Hubo un error al enviar tu mensaje. Por favor, inténtalo de nuevo más tarde.';
      },
    });
  }

  onCloseSuccess() {
    this.successMsg = '';
  }
  onCloseError() {
    this.errorMsg = '';
